"use strict";document.addEventListener("DOMContentLoaded",function(){var e=localStorage.getItem("current-theme");document.documentElement.setAttribute("data-theme",e);var t=document.querySelector(".change-theme");t.addEventListener("click",function(){"light"==document.documentElement.dataset.theme?(document.documentElement.setAttribute("data-theme","dark"),t.classList.add("dark-theme-activate"),localStorage.setItem("current-theme","dark")):(document.documentElement.setAttribute("data-theme","light"),t.classList.remove("dark-theme-activate"),localStorage.setItem("current-theme","light"))})});